FROM DEPT_MANAGE



INSERT INTO DEPT_MANAGE(APPLY_NO
						, STU_NO
						, APPLY_CODE
						, FROM_COLL
						, FROM_DEPT
						, STU_YEAR
						, STU_SEM
						, PROCESS_STATUS
				)VALUES(  #{applyNo}
						, #{stuNo}
						, #{applyCode}
						, #{fromColl}
						, #{fromDept}
						, (SELECT STU_YEAR FROM STU WHERE STU_NO = #{stuNo})
						, (SELECT STU_SEM FROM STU WHERE STU_NO = #{stuNo})
						, #{processStatus}
)

