mapper.insert:
INSERT INTO LECTURE (
	LEC_NO
	, LEC_NAME
	, LEC_SCORE
	, COLL_NO
	, DEPT_NO
	, EMP_NO
	, MAX_NUM 
	, NOW_NUM 
	, SEM_NO

) VALUES (
	#{lecNo}
	, #{lecName}
	, #{lecScore}
	, #{collNo}
	, #{deptNo}
	, #{empNo}
	, #{maxNum}
	, #{nowNum}
	, #{semNo}

)

INSERT INTO LECTURE_PDF(
	PDF_NO
	, ORIGIN_PDF_NAME
	, ATTACHED_PDF_NAME
	, LEC_NO
) VALUES (
(SELECT 'PDF_'||LPAD(NVL(MAX(TO_NUMBER(SUBSTR(PDF_NO, 5))),0)+1,3,0) FROM LECTURE_PDF)			
, #{originPdfName}
, #{attachedPdfName}
, #{lecNo}
)

INSERT INTO LECTURE_TIME(
	TIME_NO
	, LEC_DAY
	, FIRST_TIME
	, LAST_TIME
	, LEC_NO
) VALUES (
(SELECT 'TIME_'||LPAD(NVL(MAX(TO_NUMBER(SUBSTR(TIME_NO, 6))),0)+1,3,0) FROM LECTURE_TIME)			
, #{lecDay}
, #{firstTime}
, #{lastTime}
, #{lecNo}
)

mapper.update:
UPDATE SET LECTURE
SET 
	LEC_NAME = #{lecName}
	, LEC_SCORE = #{lecScore}
	, LEC_DAY = #{lecDay}
	,  FIRST_TIME =#{fristTime}
	, LAST_TIME = #{lastTime}
WHERE LEC_NO = #{lecNo}
