mapper.insert:
INSERT INTO TABLE LECTURE (
	LEC_NO
	, LEC_NAME
	, LEC_SCORE
	, COLL_NO
	, DEPT_NO
	, EMP_NO
	, MAX_NUM 
	, NOW_NUM 
	, SEM_NO
	, LEC_STATUS

) VALUES (
	(SELECT 'LEC_'||LPAD(NVL(MAX(TO_NUMBER(SUBSTR(LEC_NO, 5))),0)+1,3,0) FROM LECTURE)
	, #{lecName}
	, #{lecScore}
	, #{collNo}
	, #{deptNo}
	, #{empNo}
	, #{maxNum}
	, #{nowNum}
	, #{semNo}
	, #{lecStatus}

)
