mapper.insert:
INSERT INTO LECTURE (
	LEC_NO
	, LEC_NAME
	, LEC_SCORE
	, COLL_NO
	, DEPT_NO
	, EMP_NO
	, MAX_NUM 
	, NOW_NUM 
	, SEM_NO

) VALUES (
	#{lecNo}
	, #{lecName}
	, #{lecScore}
	, #{collNo}
	, #{deptNo}
	, #{empNo}
	, #{maxNum}
	, #{nowNum}
	, #{semNo}

)

INSERT INTO LECTURE_PDF(
	PDF_NO
	, ORIGIN_PDF_NAME
	, ATTACHED_PDF_NAME
	, LEC_NO
) VALUES (
(SELECT 'PDF_'||LPAD(NVL(MAX(TO_NUMBER(SUBSTR(PDF_NO, 5))),0)+1,3,0) FROM LECTURE_PDF)			
, #{originPdfName}
, #{attachedPdfName}
, #{lecNo}
)

INSERT INTO LECTURE_TIME(
	TIME_NO
	, LEC_DAY
	, FIRST_TIME
	, LAST_TIME
	, LEC_NO
) VALUES (
(SELECT 'TIME_'||LPAD(NVL(MAX(TO_NUMBER(SUBSTR(TIME_NO, 6))),0)+1,3,0) FROM LECTURE_TIME)			
, #{lecDay}
, #{firstTime}
, #{lastTime}
, #{lecNo}
)

mapper.update:
UPDATE  LECTURE
SET 
	LEC_NAME = #{lecName}
	, LEC_SCORE = #{lecScore}

WHERE LEC_NO = #{lecNo}

UPDATE LECTURE_TIME
SET 
	 LEC_DAY = #{lecDay}
	,  FIRST_TIME = #{firstTime}
	, LAST_TIME = #{lastTime}
WHERE LEC_NO = #{lecNo}

mapper.select:
SELECT L.LEC_NO , LEC_NAME , LEC_SCORE ,(SELECT COLL_NAME FROM COLLEAGE WHERE COLL_NO = L.COLL_NO) AS COLL_NO , (SELECT DEPT_NAME FROM DEPT WHERE DEPT_NO = L.DEPT_NO) AS DEPT_NO , (SELECT MEM_NAME FROM MEMBER WHERE MEM_NO = (SELECT MEM_NO FROM EMP WHERE EMP_NO = L.EMP_NO)) AS EMP_NO , (SELECT LEC_DAY||'/'||FIRST_TIME||'-'||LAST_TIME FROM LECTURE_TIME WHERE LEC_NO = L.LEC_NO ) AS LAST_TIME , MAX_NUM , NOW_NUM , (SELECT SEM_NAME FROM SEMESTER WHERE SEM_NO = L.SEM_NO) AS SEM_NO , PDF_NO , ORIGIN_PDF_NAME , ATTACHED_PDF_NAME , CASE WHEN MAX_NUM - NOW_NUM > 0 THEN 'Y' ELSE 'N' END AS LEC_STATUS FROM LECTURE L, LECTURE_PDF LP, LECTURE_TIME LT WHERE L.LEC_NO = LP.LEC_NO AND L.LEC_NO = LT.LEC_NO
#{lecNo}
ORDER BY LEC_NO DESC

SELECT LEC_NO
FROM ENROLLMENT
WHERE STU_NO = #{stuNo}

mapper.delete:
DELETE FROM ENROLLMENT
WHERE  STU_NO = #{stuNo} 
AND LEC_NO = #{lecNo}

DELETE FROM STU_GRADE
WHERE  STU_NO = #{stuNo} 
AND LEC_NO = #{lecNo}

mapper.insert:
INSERT INTO STU_GRADE(
    STU_GRADE_NO
    , LEC_NO
    , STU_NO
)VALUES (
    (SELECT 'STU_GRADE_' ||LPAD(NVL(MAX(TO_NUMBER(SUBSTR(STU_GRADE_NO,11))),0)+1,3,0) FROM STU_GRADE)
    , #{lecNo}
  	, #{stuNo}
)

mapper.update:
UPDATE STU_GRADE
SET GRADE = #{grade} 
WHERE STU_NO = #{stuNo} AND LEC_NO = #{lecNo}
